import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Modal, ScrollView } from 'react-native';

const MedicalConditionsList = ({ onStepChange }) => {
  // const { onSelectCondition } = route.params;
  const [isModalVisible, setModalVisible] = useState(false);

  const medicalConditions = [
    'Gallstones', 'Myocarditis', 'Infertility in Men', 'Infertility in Women', 'Chronic Instability of the Ankle', 
    'Alzheimer\'s Disease', 'Endometriosis', 'Insulin', 'Asthma of the Skin in Children', 'Hammer Finger', /* ... add other conditions */
  ];

  const handleSelectCondition = (condition) => {
    onSelectCondition(condition);
  };

  return (
    <View style={styles.container}>
      <Text>List of Medical Conditions:</Text>
      {medicalConditions.map((condition, index) => (
        <TouchableOpacity
          key={index}
          onPress={() => handleSelectCondition(condition)}
          style={styles.conditionButton}
        >
          <Text>{condition}</Text>
        </TouchableOpacity>
      ))}  
       <View style={styles.closeButtonContainer}>
      <TouchableOpacity style={styles.closeButton} onPress={() => onStepChange(3)}>
        <Text style={styles.closeButtonText}>Close</Text>
      </TouchableOpacity>
    </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  conditionButton: {
    backgroundColor: '#e0e0e0',
    borderRadius: 10,
    padding: 10,
    marginVertical: 5,
    alignItems: 'center',
  },
  modalContainer: {
    flex: 1,
    backgroundColor: 'white',
    padding: 20,
  },
  closeModalButton: {
    backgroundColor: '#454',
    borderRadius: 10,
    padding: 10,
    marginVertical: 5,
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  closeButtonContainer: {
    position: 'absolute',
    bottom: 20, // Adjust the bottom distance as needed
    width: '100%',
    alignItems: 'center',
  },
  closeButton: {
    backgroundColor: 'red', // Customize the background color
    padding: 15,
    borderRadius: 10,
  },
  closeButtonText: {
    color: 'white', // Customize the text color
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default MedicalConditionsList;

