import { useNavigation } from '@react-navigation/native';
import React from 'react';
import { StyleSheet, View, TouchableOpacity, Text } from 'react-native';

const Status = () => {
    const navigation = useNavigation();
    const onPress = (status) => {
        // כאן ניתן להעביר את המצב לדף הבא באמצעות הניווט
        navigation.navigate('ProblemType', { status });
    };

    return (
        <View style={styles.container}>
            <TouchableOpacity
                onPress={() => onPress('Safe')}
                style={[styles.button, styles.safeButton]}
            >
                <Text style={styles.buttonText}>Safe</Text>
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => onPress('In Danger')}
                style={[styles.button, styles.dangerButton]}
            >
                <Text style={styles.buttonText}>In Danger</Text>
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => onPress('Emergency')}
                style={[styles.button, styles.emergencyButton]}
            >
                <Text style={styles.buttonText}>Emergency</Text>
            </TouchableOpacity>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        width: 200,
        height: 50,
        marginVertical: 10,
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center',
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
    buttonText: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#fff',
    },
    safeButton: {
        backgroundColor: '#ffffff',
        shadowColor: '#000',
    },
    dangerButton: {
        backgroundColor: '#ffff00',
        shadowColor: '#000',
    },
    emergencyButton: {
        backgroundColor: '#ff0000',
        shadowColor: '#000',
    },
});

export default Status;
