import React from 'react';
import axios from 'axios';
import { View, Text, Button, TouchableOpacity, StyleSheet } from 'react-native';
import { sendVerificationCode } from './verifyEmailFunctions';
import {FloatingLabelInput} from 'react-native-floating-label-input';

const ResetPassword = ({ onEmailChange, onStepChange }) => {
    const [messageToUser, onChangeMessageToUser] = React.useState('');
    const [emailInput, setEmailInput] = React.useState('');

    const checkIfEmailExists = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/patients/get-by-email/`, { email })
                .then(response => {
                    console.log('Data:', response.data);
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }



    const handleVerificationCode = async () => {
        const result = await checkIfEmailExists(emailInput);
        console.log("result:", result);
        if (!result) {
            onChangeMessageToUser('not valid email');
        } else {
            onChangeMessageToUser('succeeded!');
            await sendVerificationCode(emailInput);
            onEmailChange(emailInput);// update the email input in the parent component
            onStepChange(2);//nevigate to the next step
        }
    }

    return (
        <View>
            <Text>enter an email address:</Text>
            {/* <FloatingLabelInput
                onChangeText={setEmailInput}
                value={emailInput}
                label={'your-email'}
                inputStyles={{borderColor: 'transparent'}}
            /> */}
            <Button title='send me verification code' onPress={handleVerificationCode}></Button>
            <Text>{messageToUser}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    inputContainer: {
        borderColor: 'transparent', // Set border color to transparent
      },
});
export default ResetPassword;
