import React from 'react';
import axios from 'axios';
import { View, Text, Button, TextInput } from 'react-native';

const ResetPassword = ({onStepChange}) => {
    const [email, onChangeEmail] = React.useState('');
    const [messageToUser, onChangeMessageToUser] = React.useState('');

    const checkIfEmailExists = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/patients/get-by-email/`, { email })
                .then(response => {
                    console.log('Data:', response.data);
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }

    const sendVerificationCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/initiate-reset/`, { email })
                .then(response => {
                    console.log('Data:', response.data);
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }

    const handleVerificationCode = async () => {
        const result = await checkIfEmailExists(email);
        console.log("result:", result);
        if (!result) {
            onChangeMessageToUser('not valid email');
        } else {
            onChangeMessageToUser('succeeded!');
            onStepChange(2)
        }
    }

    return (
        <View>
            <Text>enter an email address:</Text>
            <TextInput
                onChangeText={onChangeEmail}
                value={email}
            />
            <Button title='send me verification code' onPress={handleVerificationCode}></Button>
            <Text>{messageToUser}</Text>
        </View>
    )
}

export default ResetPassword;
