import React, { useState } from 'react';
import SignUpScreen from '../components/signUp/signUp1';
import SignUp2 from '../components/signUp/signUp2';
import MedicalConditionsComponent from '../components/signUp/SignUp3';
import MedicalConditionsList from '../components/signUp/MedicalConditionsList';

const SignUpPage = () => {
  const [step, setStep] = useState(1);

  const handleStepChange = (newStep) => {
    setStep(newStep);
  };

  return (
    <>
      {step === 1 && <SignUpScreen onNext={() => handleStepChange(2)} onPrev={() =>handleStepChange()}/>}
      {step === 2 && <SignUp2 onNext={() => handleStepChange(3)} onPrev={() => handleStepChange(1)} />}
      {step === 3 && <MedicalConditionsComponent onNext={() => handleStepChange(4)} onPrev={() => handleStepChange(2)} />}
      {step === 4 && <MedicalConditionsList onNext={() => handleStepChange(5)} onPrev={() =>handleStepChange(3)} />}
      {/* Add more components if needed */}

      {/* Close button
      <button onClick={() => handleStepChange(1)}>Close</button> */}
    </>
  );
};

export default SignUpPage;
