import { useNavigation } from '@react-navigation/native';
import React from 'react';
import { StyleSheet, Button, View } from "react-native";

const Status = () => {
    const navigation = useNavigation();
    const onPress = () => {
        navigation.navigate('ProblemType');
    }
    return (
        <View style={styles.container}>
            <Button
                onPress={onPress}
                title="Safe"
                color="#ffffff"
                style = {styles.button}
            />
            <Button
                onPress={onPress}
                title="In Danger"
                color="#ffff00"
                style = {styles.button}
            />
            <Button
                onPress={onPress}
                title="Emergency"
                color="#ff0000"
                style = {styles.button}
            />
        </View>
    )
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        back: '#ffffff',
        width: 200,
        height: 200,
        marginVertical: 10,
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center',
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
});


export default Status;
