import React, { useState, useRef } from 'react';
import { View, TextInput, StyleSheet } from 'react-native';
import CodeInput from './codeInput';

const EmailVerification = ({ onStepChange }) => {
    const [verificationCode, setVerificationCode] = useState(['', '', '', '', '', '']);
    const [messageToUser, onChangeMessageToUser] = React.useState('');

    const verifyCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/email-verification`, { email, verificationCode })
                .then(response => {
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error verifing code:', error);
        }

    }

    const handleVerificationCode = async () => {
        const verificationResult = await verifyCode(email);
        if (!verificationResult) {
            onChangeMessageToUser("Code not verified")
        } else {
            onChangeMessageToUser('succeeded!');
            onStepChange(2)
        }
    }

    return (
        <View style={styles.container}>
            <CodeInput></CodeInput>
            <Button title='Verify' onPress={handleVerificationCode}></Button>
            <TouchableWithoutFeedback onPress={onPress}>
                <View>
                    <Text style={{ color: 'blue' }}>Resend</Text>
                </View>
            </TouchableWithoutFeedback>
        </View>
    );
};

const styles = StyleSheet.create({

});

export default EmailVerification;
