import React, { useState } from 'react';
import { StyleSheet, View, TouchableOpacity, TextInput, Text } from 'react-native';

import { useNavigation } from '@react-navigation/native';

const ProblemType = ({ route }) => {
    const navigation = useNavigation();
    const level = route.params;
    const [anotherProblem, setAnotherProblem] = useState('');
    const [isFieldValid, setIsFieldValid] = useState(false);

    const changeText = (text) => {
        setAnotherProblem(text);
        setIsFieldValid(true);

    }
    const handlePress = (problem) => {
        const prob = { problem: problem }
        const paramsToSend = JSON.stringify({ ...level, ...prob })
        console.log(paramsToSend);
        navigation.navigate('SendAlert', paramsToSend)
    };
    const problems = ["Event", "Medical condition"];


    return (
        <View style={styles.container}>
            <Text>Choose your problem: </Text>
            {problems.map((item, index) => (
                <TouchableOpacity style={styles.button} key={index} onPress={() => handlePress(item)}>
                    <Text style={styles.buttonText}>{item}</Text>
                </TouchableOpacity>
            ))}
            <View style={styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="Another problem"
                    onChangeText={newText => changeText(newText)}
                    value={anotherProblem}
                />
                {!isFieldValid && (
                    <Text style={{ color: 'red' }}>Mandatory field - please enter a value in the field</Text>
                )}
                <TouchableOpacity style={styles.submitButton} onPress={() => { isFieldValid ? handlePress(anotherProblem):"" }}>
                    <Text style={styles.submitButtonText}>OK</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        width: 200,
        height: 50,
        backgroundColor: 'blue',
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 10,
        borderRadius: 10,
    },
    buttonText: {
        color: 'white',
        fontSize: 18,
    },
    inputContainer: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    input: {
        height: 50,
        borderColor: 'blue',
        borderWidth: 1,
        width: 140,
        marginVertical: 10,
        borderRadius: 5,
        paddingHorizontal: 10,
    },
    submitButton: {
        width: 50,
        height: 50,
        backgroundColor: 'blue',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginLeft: 10,
    },
    submitButtonText: {
        color: 'white',
        fontSize: 16,
    },
});

export default ProblemType;
