import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import EmailVerification from '../components/forget_password/email_verification';
import ResetPassword from '../components/forget_password/reset_password';
import CompleteResetPassword from '../components/forget_password/complete_reset_password';



export default function ForgetPassword() {
    const [email, onChangeEmail] = React.useState('');
    const [step, setStep] = useState(1);
    const handleEmailChange = (newEmail) => {
        onChangeEmail(newEmail);
    };
    const handleStepChange = (newStep) => {
        setStep(newStep);
    };

    return (
        <>
            {step === 1 && <ResetPassword onEmailChange={handleEmailChange} onStepChange={handleStepChange} emi/>}
            {step === 2 && <EmailVerification onStepChange={[email, handleStepChange]} />}
            {step === 3 && <CompleteResetPassword onStepChange={handleStepChange} />}
        </>

    );
}
