import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import EmailVerification from './components/forget_password/email_verification';
import CompleteResetPassword from './components/forget_password/reset_password';
import ResetPassword from './components/'



export default function ForgetPassword() {
    const [step, setStep] = useState(1);
    const handleStepChange = (newStep) => {
        setStep(newStep);
    };

    return (
        <>
            {step === 1 && <ResetPassword onStepChange={handleStepChange} />}
            {step === 2 && <EmailVerification onStepChange={handleStepChange} />}
            {step === 3 && <CompleteResetPassword onStepChange={handleStepChange} />}
        </>

    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});
