import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Modal, ScrollView } from 'react-native';

const MedicalConditionsList = ({ route, navigation }) => {
  const { onSelectCondition } = route.params;
  const [isModalVisible, setModalVisible] = useState(false);

  const medicalConditions = [
    'Gallstones', 'Myocarditis', 'Infertility in Men', 'Infertility in Women', 'Chronic Instability of the Ankle', 
    'Alzheimer\'s Disease', 'Endometriosis', 'Insulin', 'Asthma of the Skin in Children', 'Hammer Finger', /* ... add other conditions */
  ];

  const handleSelectCondition = (condition) => {
    onSelectCondition(condition);
  };

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const renderMedicalConditions = () => {
    return medicalConditions.map((condition, index) => (
      <TouchableOpacity
        key={index}
        onPress={() => handleSelectCondition(condition)}
        style={styles.conditionButton}
      >
        <Text>{condition}</Text>
      </TouchableOpacity>
    ));
  };

  return (
    <View style={styles.container}>
      <Text>List of Medical Conditions:</Text>
      {medicalConditions.map((condition, index) => (
        <TouchableOpacity
          key={index}
          onPress={() => handleSelectCondition(condition)}
          style={styles.conditionButton}
        >
          <Text>{condition}</Text>
        </TouchableOpacity>
      ))}

      {/* Modal for displaying medical conditions */}
      <Modal visible={isModalVisible} animationType="slide" transparent={true}>
        <View style={styles.modalContainer}>
          <ScrollView>
            {renderMedicalConditions()}
            {/* Close button for the modal */}
            <TouchableOpacity onPress={() => { toggleModal(); navigation.goBack(); }} style={styles.closeModalButton}>
              <Text>Close</Text>
            </TouchableOpacity>
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  conditionButton: {
    backgroundColor: '#e0e0e0',
    borderRadius: 10,
    padding: 10,
    marginVertical: 5,
    alignItems: 'center',
  },
  modalContainer: {
    flex: 1,
    backgroundColor: 'white',
    padding: 20,
  },
  closeModalButton: {
    backgroundColor: '#444444',
    borderRadius: 10,
    padding: 10,
    marginVertical: 5,
    alignItems: 'center',
  },
});

export default MedicalConditionsList;


