import React, { useState } from 'react';
import { View, Text, Pressable, StyleSheet, Platform } from 'react-native';
import { signUpValidationSchema } from '../../config/validations';
import { MaterialIcons } from '@expo/vector-icons';
// import DateTimePicker from '@react-native-community/datetimepicker';
// import DatePicker from 'react-native-datepicker';

const SignUp2 = ({ onStepChange }) => {
  const [formData, setFormData] = useState({
    phoneNumber: '',
    country: '',
    city: '',
    street: '',
    streetNum: '',
    entrance: '',
    houseNumber: '',
    dob: new Date(), // Set default date to current date
  });

  const [errors, setErrors] = useState({});
  const [showDatePicker, setShowDatePicker] = useState(false);

  const handleInputChange = (key, value) => {
    setFormData({ ...formData, [key]: value });
    setErrors({ ...errors, [key]: null });
  };

  // Update isFieldValid to handle date validation
  const isFieldValid = (key) => {
    if (key === 'dob') {
      // Check if date is selected
      return formData[key] !== null;
    }

    if (formData[key] && !errors[key]) {
      try {
        signUpValidationSchema.validateSyncAt(key, formData);
        return true;
      } catch (validationError) {
        return false;
      }
    }
    return false;
  };

  const renderLabel = (key) => {
    return (
      <Text style={styles.placeholderLabel}>
        {formData[key] ? key.charAt(0).toUpperCase() + key.slice(1) : ''}
      </Text>
    );
  };

  const handleSignUp = (newStep) => {
    const formErrors = {};
    Object.keys(formData).forEach((key) => {
      try {
        signUpValidationSchema.validateSyncAt(key, formData);
      } catch (validationError) {
        formErrors[key] = validationError.message;
      }
    });

    if (Object.keys(formErrors).length === 0) {
      setErrors({});
      onStepChange(newStep);
    } else {
      setErrors(formErrors);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign Up - Step 2</Text>

      {Object.keys(formData).map((key) => (
        <View key={key} style={styles.inputContainer}>
          {renderLabel(key)}
          {key === 'dob' && Platform.OS === 'ios' ? (
            <View>
              <Pressable onPress={() => setShowDatePicker(true)}>
                <Text>{formData[key].toLocaleDateString()}</Text>
              </Pressable>
              {showDatePicker && (
                <DateTimePicker
                  value={formData[key]}
                  mode="date"
                  display="default"
                  onChange={(event, date) => {
                    setShowDatePicker(Platform.OS === 'ios');
                    handleInputChange(key, date);
                  }}
                />
              )}
            </View>
          ) : key === 'dob' ? (
            <DatePicker
              style={{ width: '80%' }}
              date={formData[key]}
              mode="date"
              placeholder="Select Date of Birth"
              format="YYYY-MM-DD"
              confirmBtnText="Confirm"
              cancelBtnText="Cancel"
              customStyles={{
                dateIcon: {
                  position: 'absolute',
                  left: 0,
                  top: 4,
                  marginLeft: 0,
                },
                dateInput: {
                  borderWidth: 0,
                },
              }}
              onDateChange={(date) => handleInputChange(key, date)}
            />
          ) : (
            <TextInput
              style={{
                ...styles.input,
                height: 40,
                borderColor: isFieldValid(key) ? 'green' : errors[key] ? 'red' : 'gray',
              }}
              placeholder={key.charAt(0).toUpperCase() + key.slice(1)}
              onChangeText={(text) => handleInputChange(key, text)}
              value={key === 'dob' ? formData[key].toLocaleDateString() : formData[key]}
              keyboardType={key === 'dob' ? 'numeric' : 'default'}
              editable={key !== 'dob'}
            />
          )}
          {isFieldValid(key) && (
            <View style={styles.iconContainer}>
              <MaterialIcons name="check" size={24} color="green" style={styles.icon} />
            </View>
          )}
          {errors[key] && <Text style={styles.error}>{errors[key]}</Text>}
        </View>
      ))}

      <View style={styles.buttonContainer}>
        <Pressable style={styles.buttonPrev} onPress={() => onStepChange(1)}>
          <Text style={styles.buttonText}>Prev</Text>
        </Pressable>

        <Pressable style={styles.buttonNext} onPress={() => handleSignUp(3)}>
          <Text style={styles.buttonText}>Next</Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  inputContainer: {
    position: 'relative',
    marginBottom: 20,
  },
  input: {
    width: '80%',
    borderWidth: 1,
    paddingLeft: 10,
    flexDirection: 'row',
    alignItems: 'center', // Center the icon vertically
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  error: {
    color: 'red',
    marginTop: 5,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '80%',
    marginTop: 20,
  },
  buttonPrev: {
    backgroundColor: '#ccc',
    padding: 10,
    borderRadius: 5,
    flex: 1,
    marginRight: 10,
  },
  buttonNext: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    flex: 1,
  },
  icon: {
    marginLeft: 10, // Adjust the margin as needed
  },
  iconContainer: {
    position: 'absolute',
    top: 12,
    right: 12,
  },
  placeholderLabel: {
    position: 'absolute',
    left: 10,
    top: -8,
    backgroundColor: 'rgb(243, 243, 243), 195, 197',
    paddingHorizontal: 5,
    color: 'black',
  },
});

export default SignUp2;
