import React, { useState } from 'react';
import EmailVerification from '../components/forget_password/email_verification';
import ResetPassword from '../components/forget_password/reset_password';
import CompleteResetPassword from '../components/forget_password/complete_reset_password';
import ResetSuccessfully from '../components/forget_password/reset_successfully'



export default function ForgetPassword() {
    const [email, setEmail] = React.useState('');
    const [step, setStep] = useState(1);
    const handleEmailChange = (newEmail) => {
        setEmail(newEmail);
    };
    const handleStepChange = (newStep) => {
        setStep(newStep);
    };

    return (
        <>
            {step === 1 && <ResetPassword onEmailChange={handleEmailChange} onStepChange={handleStepChange} />}
            {step === 2 && <EmailVerification email={email} onStepChange={handleStepChange} />}
            {step === 3 && <CompleteResetPassword email={email} onStepChange={handleStepChange} />}
            {step === 4 && <ResetSuccessfully />}
        </>

    );
}
