import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
  StyleSheet,
  TouchableOpacity,
} from 'react-native';
import { signUpValidationSchema } from '../../config/validations';
import { MaterialIcons } from '@expo/vector-icons';
import DateTimePicker from '@react-native-community/datetimepicker';

const SignUp2 = ({ onStepChange }) => {
  const [formData, setFormData] = useState({
    // ... (your existing form data)
    dateOfBirth: new Date(), // Initialize with the current date
  });

  const [errors, setErrors] = useState({});
  const [showDatePicker1, setShowDatePicker] = useState(false);

  const fieldDisplayNames = {
    // ... (your existing field display names)
    dateOfBirth: 'Date of birth',
  };

  const handleInputChange = (key, text) => {
    setFormData({ ...formData, [key]: text });
    setErrors({ ...errors, [key]: null });
  };

  const isFieldValid = (key) => {
    if (formData[key] && !errors[key]) {
      try {
        signUpValidationSchema.validateSyncAt(key, formData);
        return true;
      } catch (validationError) {
        return false;
      }
    }
    return false;
  };

  const renderLabel = (key) => (
    <Text style={styles.placeholderLabel}>
      {formData[key] ? fieldDisplayNames[key] : ''}
    </Text>
  );

  const handleSignUp = (newStep) => {
    const formErrors = {};
    Object.keys(formData).forEach((key) => {
      try {
        signUpValidationSchema.validateSyncAt(key, formData);
      } catch (validationError) {
        formErrors[key] = validationError.message;
      }
    });

    if (Object.keys(formErrors).length === 0) {
      setErrors({});
      onStepChange(newStep);
    } else {
      setErrors(formErrors);
    }
  };

  const handleDateChange = (event, selectedDate) => {
    setShowDatePicker(false);
    if (selectedDate) {
      setFormData({ ...formData, dateOfBirth: selectedDate });
    }
  };

  const showDatePicker = () => {
    setShowDatePicker(true);
  };

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={{ flex: 1 }}
    >
      <ScrollView contentContainerStyle={styles.container}>
        <Text style={styles.title}>Sign Up - Step 2</Text>

        {Object.keys(formData).map((key) => (
          <View key={key} style={styles.inputContainer}>
            {renderLabel(key)}
            <TextInput
              style={{
                ...styles.input,
                height: 40,
                borderColor: isFieldValid(key) ? 'green' : errors[key] ? 'red' : 'gray',
              }}
              placeholder={fieldDisplayNames[key]}
              onChangeText={(text) => handleInputChange(key, text)}
              value={key === 'dateOfBirth' ? formData[key].toDateString() : formData[key]}
              keyboardType={key === 'dateOfBirth' ? 'numeric' : 'default'}
              onFocus={key === 'dateOfBirth' ? showDatePicker : undefined}
            />
            {isFieldValid(key) && (
              <View style={styles.iconContainer}>
                <MaterialIcons name="check" size={24} color="green" style={styles.icon} />
              </View>
            )}
            {errors[key] && <Text style={styles.error}>{errors[key]}</Text>}
          </View>
        ))}

        <TouchableOpacity style={styles.dateButton} onPress={showDatePicker}>
          <Text style={styles.buttonText}>Select Date</Text>
        </TouchableOpacity>

        <View style={styles.buttonContainer}>
          <Pressable style={styles.buttonPrev} onPress={() => onStepChange(1)}>
            <Text style={styles.buttonText}>Prev</Text>
          </Pressable>

          <Pressable style={styles.buttonNext} onPress={() => handleSignUp(3)}>
            <Text style={styles.buttonText}>Next</Text>
          </Pressable>
        </View>

        {showDatePicker && (
          <DateTimePicker
            value={formData.dateOfBirth}
            mode="date"
            display="default"
            onChange={handleDateChange}
          />
        )}
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

const styles = StyleSheet.create({
  // ... (your existing styles)
});
export default SignUp2;
