import React, { useState } from 'react';
import axios from 'axios';
import { Button, View, Text, TouchableWithoutFeedback, StyleSheet } from 'react-native';
import CodeInput from './codeInput';
import { sendVerificationCode } from './verifyEmailFunctions';



const EmailVerification = ({ email, onStepChange }) => {
    const [verificationCode, setVerificationCode] = useState('');
    //const [errorMessage, seterrorMessage] = React.useState('');
    //const [message, setMessage] = React.useState('');
    const [errorType, setErrorType] = React.useState('');

    const handleCodeChange = (code) => {
        setVerificationCode(code);
    };

    const verifyCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/email-verification`, { email, verificationCode })
                .then(response => {
                    return response.data;
                })
                .catch(error => {
                    switch(error.response.data.errorCode){
                        case "EXPIRED_CODE": 
                    }
                });
        }
        catch (error) {
            console.error('Error verifing code:', error);
        }

    }

    const handleVerificationCode = async () => {
        const verificationResult = await verifyCode(email);
        if (!verificationResult) {
            if (errorType === "EXPIRED_CODE") {
                seterrorMessage("The code has expired. Please resend the code")
            }
            if (errorType === "INVALID_CODE_ERROR") {
                seterrorMessage("Invalid code")
            }
            if (errorType === "TOO_MUCH_ATTEMPTS") {
                seterrorMessage("Sorry, but you have exceeded the number of attempts allowed...\nPlease try again in an hour")
            }

        } else {
            seterrorMessage('');
            onStepChange(3);//nevigate to the next step
        }
    }
    const handleResend = async () => {
        sendVerificationCode(email);
        setMessage("Verification code sent successfully");
    }

    return (
        <View style={styles.container}>
            <Text>Check your email inbox</Text>
            <Text>a verification code has been sent to you right now</Text>
            <CodeInput onCodeChange={handleCodeChange}></CodeInput>
            <Button title='Verify' onPress={handleVerificationCode}></Button>
            <TouchableWithoutFeedback onPress={() => {handleResend}}>
                <View>
                    <Text style={{ color: 'blue', cursor: 'pointer' }}>Resend</Text>
                </View>
            </TouchableWithoutFeedback>
            <Text style={styles.errorMessage}>{errorMessage}</Text>
        </View>
    );
};

const styles = StyleSheet.create({
    errorMessage: {
        color: "red"
    },
    message: {
        color: 'green'
    }
});

export default EmailVerification;
