import React from 'react';
import axios from 'axios';
import { View, Text, Button, TextInput } from 'react-native';

const ForgetPassword = ({}) => {
    const [email, onChangeEmail] = React.useState('');
    const [massageToUser, onChangeMassageToUser] = React.useState('');

    const checkIfEmailExists = async (email) => {
        try {
            return await axios.get(`http://localhost:3000/${email}`)
                .then(response => {
                    console.log('Data:', response.data);
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }

    const handleVerificationCode = async () => {
        if (!await checkIfEmailExists(email)) {
          onChangeMessageToUser('not valid email');
        } else {
          onChangeMessageToUser('succeeded!');
          //navigation.navigate('EmailVerification');
        }
      }

    return (
        <View>
            <Text>enter an email address:</Text>
            <TextInput
                onChangeText={onChangeEmail}
                value={email}
            />
            <Button title='send me verification code' onPress={handleVerificationCode}></Button>
            <Text>{massageToUser}</Text>
        </View>
    )
}

export default ForgetPassword;
