import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Alert } from 'react-native';
// import { useNavigation } from '@react-navigation/native';

const SignUpScreen =  ({ onNext, onPrev }) => {
    // const navigation = useNavigation();  // Get navigation prop using useNavigation

    // State to manage user input
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // State for error messages
    const [error, setError] = useState('');

    // Function to handle form validation
    const validateForm = () => {
        setError('');

        if (!firstname) {
            setError('All fields are required');
            return false;
        }

        // if (!firstname || !lastname || !email || !password) {
        //     setError('All fields are required');
        //     return false;
        // }

        // Add more sophisticated validation logic if needed

        return true;
    };

    // Function to handle sign-up action
    // const handleSignUp = () => {
    //     if (validateForm()) {
    //         //Navigate to SignUp2 when the form is valid
    //          navigation.navigate('SignUp2', { firstname, lastname, email, password });
    //         // // Perform sign-up logic here (e.g., make an API request)
    //     ;
    //     }
    // };

    const handleSignUp = (value) => {
        if (validateForm()) {
          onNext(value); // Call the callback to move to the next step
        }
    }

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Sign Up</Text>

            <TextInput
                style={styles.input}
                placeholder="Firstname"
                onChangeText={(text) => setFirstname(text)}
                value={firstname}
            />

            <TextInput
                style={styles.input}
                placeholder="Lastname"
                onChangeText={(text) => setLastname(text)}
                value={lastname}
            />

            <TextInput
                style={styles.input}
                placeholder="Email"
                onChangeText={(text) => setEmail(text)}
                value={email}
                keyboardType="email-address"
            />

            <TextInput
                style={styles.input}
                placeholder="Password"
                onChangeText={(text) => setPassword(text)}
                value={password}
                secureTextEntry
            />

            {/* Display error message if there is one */}
            {error ? <Text style={styles.error}>{error}</Text> : null}

            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.buttonPrev} onPress={onPrev}>
                <Text style={styles.buttonText}>Prev</Text>
                </TouchableOpacity>

                <TouchableOpacity style={styles.buttonNext} onPress={handleSignUp(2)}>
                <Text style={styles.buttonText}>Next</Text>
                </TouchableOpacity> 
            </View>


            {/* <TouchableOpacity style={styles.button} onPress={handleSignUp}>
                <Text style={styles.buttonText}>Prev</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.button} onPress={handleSignUp}>
                <Text style={styles.buttonText}>Next</Text>
            </TouchableOpacity> */}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    input: {
        width: '80%',
        height: 40,
        borderColor: 'gray',
        borderWidth: 1,
        marginBottom: 10,
        paddingLeft: 10,
    },
    buttonText: {
        color: 'white',
        fontWeight: 'bold',
        textAlign: 'center',
    },
    error: {
        color: 'red',
        marginBottom: 10,
    },
    
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '80%',
    marginTop: 20,
  },
  buttonPrev: {
    backgroundColor: '#ccc', // Light gray
    padding: 10,
    borderRadius: 5,
    flex: 1,
    marginRight: 10,
  },
  buttonNext: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    flex: 1,
  },
});

export default SignUpScreen;








