import React, { useState } from 'react';
import { View, Text, TouchableOpacity, TextInput, ScrollView, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const MedicalConditionsComponent = () => {
  const navigation = useNavigation();  // Get navigation prop using useNavigation

  const [selectedConditions, setSelectedConditions] = useState([]);
  const [newCondition, setNewCondition] = useState('');
   // State for error messages
   const [error, setError] = useState('');

  setError('All fields are required');

 // Function to handle form validation
 const validateForm = () => {
    setError('');

    if (!selectedConditions || !newCondition) {
        setError('All fields are required');
        return false;
    }

    // Add more sophisticated validation logic if needed

    return true;
};

  const addCondition = () => {
    if (newCondition.trim() !== '') {
      setSelectedConditions([...selectedConditions, newCondition.trim()]);
      setNewCondition('');
    }
  };

  const removeCondition = (condition) => {
    const updatedConditions = selectedConditions.filter((c) => c !== condition);
    setSelectedConditions(updatedConditions);
  };

  const renderConditions = () => {
    return selectedConditions.map((condition, index) => (
      <View key={index} style={styles.conditionBox}>
        <Text>{condition}</Text>
        <TouchableOpacity onPress={() => removeCondition(condition)}>
          <Text style={styles.removeButton}>X</Text>
        </TouchableOpacity>
      </View>
    ));
  };

  const handleSignUp3 = () => {
    if (validateForm()) {
        //Navigate to SignUp3 when the form is valid
         navigation.navigate('MedicalConditionsComponent', { firstname, lastname, email, password });
    }
};

  return (
    <View style={styles.container}>
      <ScrollView>
        <Text>Select Medical Conditions:</Text>
        {/* Add your list of medical conditions here */}
        {/* For simplicity, I'm just displaying the alphabetical sections */}
        <Text>A</Text>
        {/* ... (Add other sections similarly) */}
        <Text>H</Text>
        {/* ... (Add other sections similarly) */}
        <Text>J</Text>
        {/* ... (Add other sections similarly) */}
        <Text>M</Text>
        {/* ... (Add other sections similarly) */}
        <Text>N</Text>
        {/* ... (Add other sections similarly) */}
        <Text>S</Text>
        {/* ... (Add other sections similarly) */}
        <Text>P</Text>
        {/* ... (Add other sections similarly) */}
        <Text>K</Text>
        {/* ... (Add other sections similarly) */}
        <Text>R</Text>
        {/* ... (Add other sections similarly) */}
        <Text>T</Text>
        {/* ... (Add other sections similarly) */}
      </ScrollView>

      {/* Display selected conditions */}
      <View style={styles.selectedConditionsContainer}>{renderConditions()}</View>

      {/* Input for adding new condition */}
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Add a new medical condition"
          value={newCondition}
          onChangeText={(text) => setNewCondition(text)}
        />
        <TouchableOpacity onPress={addCondition}>
          <Text style={styles.addButton}>Add</Text>
        </TouchableOpacity>
      </View>

      {/* Login button */}
      <TouchableOpacity style={styles.loginButton}>
        <Text style={{ color: 'white' }}>Login</Text>
      </TouchableOpacity>
{/* 
      <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.buttonPrev} onPress={() => navigation.goBack()}>
          <Text style={styles.buttonText}>Prev</Text>
          </TouchableOpacity>
           <TouchableOpacity style={styles.buttonNext} onPress={handleSignUp3}>
          <Text style={styles.buttonText}>Next</Text>
          </TouchableOpacity> 
      </View> */}

    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  selectedConditionsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginVertical: 10,
  },
  conditionBox: {
    backgroundColor: '#e0e0e0',
    borderRadius: 20,
    padding: 10,
    margin: 5,
    flexDirection: 'row',
    alignItems: 'center',
  },
  removeButton: {
    marginLeft: 5,
    color: 'red',
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
  },
  input: {
    flex: 1,
    borderWidth: 1,
    borderRadius: 10,
    padding: 10,
    marginRight: 10,
  },
  addButton: {
    color: 'blue',
  },
  loginButton: {
    backgroundColor: 'blue',
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
    marginTop: 20,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '80%',
    marginTop: 20,
  },
  buttonPrev: {
    backgroundColor: '#ccc', // Light gray
    padding: 10,
    borderRadius: 5,
    flex: 1,
    marginRight: 10,
  },
  buttonNext: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    flex: 1,
  },
});

export default MedicalConditionsComponent;
