// SignUp2.js
import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import axios from 'axios';
import { signUpValidationSchema } from './Validation';

const SignUp2 = ({ onStepChange }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [country, setCountry] = useState('');
  const [city, setCity] = useState('');
  const [street, setStreet] = useState('');
  const [streetNum, setStreetNum] = useState('');
  const [entrance, setEntrance] = useState('');
  const [houseNumber, setHouseNumber] = useState('');
  const [dob, setDob] = useState('');
  const [phoneNumberError, setPhoneNumberError] = useState('');
  const [dobError, setDobError] = useState('');

  useEffect(() => {
    setPhoneNumberError('');
    setDobError('');
  }, [phoneNumber, dob]);

  const validateForm = async () => {
    setPhoneNumberError('');
    setDobError('');

    try {
      await signUpValidationSchema.validate({
        phoneNumber,
        country,
        city,
        street,
        streetNum,
        entrance,
        houseNumber,
        dob,
      });

      return true;
    } catch (validationError) {
      validationError.errors.forEach(({ path, message }) => {
        if (path === 'phoneNumber') {
          setPhoneNumberError(message);
        } else if (path === 'dob') {
          setDobError(message);
        }
      });

      return false;
    }
  };

  const handleSignUp = async (newStep) => {
    if (await validateForm()) {
      onStepChange(newStep);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign Up - Step 2</Text>

      <TextInput
        style={[
          styles.input,
          { borderColor: phoneNumberError ? 'red' : 'green' },
        ]}
        placeholder="Phone Number"
        onChangeText={(text) => setPhoneNumber(text)}
        value={phoneNumber}
        keyboardType="phone-pad"
      />
      {phoneNumberError ? <Text style={styles.error}>{phoneNumberError}</Text> : null}

      <Text style={styles.subtitle}>Exact Residential Address:</Text>

      {/* Other address input fields... */}

      <TextInput
        style={styles.input}
        placeholder="Date of Birth (YYYY-MM-DD)"
        onChangeText={(text) => setDob(text)}
        value={dob}
        keyboardType="numeric"
      />
      {dobError ? <Text style={styles.error}>{dobError}</Text> : null}

      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.buttonPrev} onPress={() => onStepChange(1)}>
          <Text style={styles.buttonText}>Prev</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.buttonNext} onPress={() => handleSignUp(3)}>
          <Text style={styles.buttonText}>Next</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
  },
  error: {
    color: 'red',
    marginBottom: 10,
},
  button: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '80%',
    marginTop: 20,
  },
  buttonPrev: {
    backgroundColor: '#ccc', // Light gray
    padding: 10,
    borderRadius: 5,
    flex: 1,
    marginRight: 10,
  },
  buttonNext: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    flex: 1,
  },
});

export default SignUp2;
