import React, { useState } from 'react';
import { View, Text, TouchableOpacity, TextInput, ScrollView, StyleSheet } from 'react-native';

const MedicalConditionsComponent = ({onStepChange}) => {

  const [selectedConditions, setSelectedConditions] = useState([]);
  const [newCondition, setNewCondition] = useState('');

  const addCondition = (condition) => {
    setSelectedConditions([...selectedConditions, condition]);
  };

  const removeCondition = (condition) => {
    const updatedConditions = selectedConditions.filter((c) => c !== condition);
    setSelectedConditions(updatedConditions);
  };

  const renderConditions = () => {
    return selectedConditions.map((condition, index) => (
      <View key={index} style={styles.conditionBox}>
        <Text>{condition}</Text>
        <TouchableOpacity onPress={() => removeCondition(condition)}>
          <Text style={styles.removeButton}>X</Text>
        </TouchableOpacity>
      </View>
    ));
  };

  const handleSelectConditions = () => {
    onStepChange(4); // Call the callback to move to another step
}

  return (
    <View style={styles.container}>
 {/* Select Medical Conditions button */}
 <TouchableOpacity onPress={handleSelectConditions} style={styles.selectConditionsButton}>
        <Text style={styles.selectConditionsButtonText}>Select Medical Conditions</Text>
      </TouchableOpacity>


      {/* Display selected conditions */}
      <View style={styles.selectedConditionsContainer}>{renderConditions()}</View>

      {/* Input for adding new condition */}
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Add a new medical condition"
          value={newCondition}
          onChangeText={(text) => setNewCondition(text)}
        />
        <TouchableOpacity onPress={() => addCondition(newCondition)}>
          <Text style={styles.addButton}>Add</Text>
        </TouchableOpacity>
      </View>

      {/* Login button */}
      <TouchableOpacity style={styles.loginButton}>
        <Text style={styles.buttonText}>Register</Text>
      </TouchableOpacity>
      

      <View style={styles.buttonContainer}>
<TouchableOpacity style={styles.buttonPrev} onPress={() =>onStepChange(2)}>
<Text style={styles.buttonText}>Prev</Text>
</TouchableOpacity>
</View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  selectedConditionsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginVertical: 10,
  },
  conditionBox: {
    backgroundColor: '#e0e0e0',
    borderRadius: 20,
    padding: 10,
    margin: 5,
    flexDirection: 'row',
    alignItems: 'center',
  },
  removeButton: {
    marginLeft: 5,
    color: 'red',
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
  },
  input: {
    flex: 1,
    borderWidth: 1,
    borderRadius: 10,
    padding: 10,
    marginRight: 10,
  },
  addButton: {
    color: 'blue',
  },
  loginButton: {
    backgroundColor: 'blue',
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
    marginTop: 20,
  },
  selectConditionsButton: {
    backgroundColor: 'yellow',
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
    marginTop: 20,
  },
  selectConditionsButtonText: {
    color: 'black',
    fontSize: 18,
    fontWeight: 'bold',
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '80%',
    marginTop: 20,
  },
  buttonPrev: {
    backgroundColor: 'blue',
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
    marginTop: 20,
    // backgroundColor: '#ccc', // Light gray
    // padding: 10,
    // borderRadius: 5,
    // flex: 1,
    // marginRight: 10,
  },
  buttonNext: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    flex: 1,
  },
});

export default MedicalConditionsComponent;
