import React, { useState } from 'react';
import { View, Text, TouchableOpacity, TextInput, ScrollView, StyleSheet, Modal } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

const MedicalConditionsComponent = () => {
  const [selectedConditions, setSelectedConditions] = useState([]);
  const [newCondition, setNewCondition] = useState('');
  const [isModalVisible, setModalVisible] = useState(false);

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const addCondition = (condition) => {
    setSelectedConditions([...selectedConditions, condition]);
  };

  const removeCondition = (condition) => {
    const updatedConditions = selectedConditions.filter((c) => c !== condition);
    setSelectedConditions(updatedConditions);
  };

  const resetComponent = () => {
    setSelectedConditions([]);
    setNewCondition('');
    setModalVisible(false);
  };

  const renderConditions = () => {
    return selectedConditions.map((condition, index) => (
      <View key={index} style={styles.conditionBox}>
        <Text>{condition}</Text>
        <TouchableOpacity onPress={() => removeCondition(condition)}>
          <Text style={styles.removeButton}>X</Text>
        </TouchableOpacity>
      </View>
    ));
  };

  const renderMedicalConditions = () => {
    // Add your list of medical conditions here
    const medicalConditions = [
      'Gallstones',
      'Reddish Earth',
      'Myocardial Infarction',
      'Infertility in Men',
      // ... (Add other conditions similarly)
    ];

    return medicalConditions.map((condition, index) => (
      <TouchableOpacity key={index} onPress={() => addCondition(condition)}>
        <Text style={styles.medicalCondition}>{condition}</Text>
      </TouchableOpacity>
    ));
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={toggleModal} style={styles.showConditionsButton}>
        <Text style={styles.showConditionsButtonText}>Select Medical Conditions</Text>
        <MaterialIcons name="check-circle" size={24} color="#4CAF50" />
      </TouchableOpacity>

      {/* Modal for displaying medical conditions */}
      <Modal visible={isModalVisible} animationType="slide" transparent={true}>
        <View style={styles.modalContainer}>
          <ScrollView>
            {renderMedicalConditions()}
            {/* Close button for the modal */}
            <TouchableOpacity onPress={toggleModal} style={styles.closeModalButton}>
              <Text>Close</Text>
            </TouchableOpacity>
          </ScrollView>
        </View>
      </Modal>

      {/* Display selected conditions */}
      <View style={styles.selectedConditionsContainer}>{renderConditions()}</View>

      {/* Input for adding new condition */}
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Add a new medical condition"
          value={newCondition}
          onChangeText={(text) => setNewCondition(text)}
        />
        <TouchableOpacity onPress={() => addCondition(newCondition)}>
          <Text style={styles.addButton}>Add</Text>
        </TouchableOpacity>
      </View>

      {/* Login button */}
      <TouchableOpacity onPress={resetComponent} style={styles.loginButton}>
        <Text style={styles.loginButtonText}>Login</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  showConditionsButton: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 20,
    backgroundColor: '#223', // Green color
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  showConditionsButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  modalContainer: {
    flex: 1,
    backgroundColor: 'white',
    padding: 20,
  },
  medicalCondition: {
    fontSize: 16,
    marginBottom: 10,
  },
  closeModalButton: {
    marginTop: 10,
    padding: 10,
    backgroundColor: '#e0e0e0',
    borderRadius: 10,
    alignItems: 'center',
  },
  selectedConditionsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginVertical: 10,
  },
  conditionBox: {
    backgroundColor: '#e0e0e0',
    borderRadius: 20,
    padding: 10,
    margin: 5,
    flexDirection: 'row',
    alignItems: 'center',
  },
  removeButton: {
    marginLeft: 5,
    color: 'red',
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
  },
  input: {
    flex: 1,
    borderWidth: 1,
    borderRadius: 10,
    padding: 10,
    marginRight: 10,
  },
  addButton: {
    color: 'blue',
  },
  loginButton: {
    backgroundColor: '#4CAF50', // Green color
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
    marginTop: 20,
  },
  loginButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default MedicalConditionsComponent;





