import React from 'react';
import { View, Text, Button, TextInput } from 'react-native';


const CompleteResetPassword = ({ onStepChange }) => {
    const [password, onChangePwd] = React.useState('');
    const [verifyPassword, onChangeVerifyPassword] = React.useState('');

    const resetPassword = async (password) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/complete-reset`, { password })
                .then(response => {
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error verifing code:', error);
        }

    }

    const handleResetPassword = async () => {
        if(password != verifyPassword) return;
        else {
            const verificationResult = await resetPassword(password);
        
            onStepChange(4);//nevigate to Login
        }
    }

    return (
        <View>
            <Text>enter an email address:</Text>
            <TextInput
                onChangeText={onChangePwd}
                value={password}
            />
            <TextInput
                onChangeText={onChangeVerifyPwd}
                value={verifyPwd}
            />
            <Button title='verify password' onPress={() => console.log(pwd === verifyPwd ? "yes" : "No")}></Button>
        </View>
    )
}

export default CompleteResetPassword;
