// import { useNavigation } from '@react-navigation/native';
import React, { useEffect, useRef } from 'react';
import { StyleSheet, Button, View, TouchableOpacity, Animated, Easing, Text } from 'react-native';

const SosButton = ({ navigation }) => {
    const animatedValue = useRef(new Animated.Value(0)).current;

    const startAnimation = () => {
        Animated.loop(
            Animated.sequence([
                Animated.timing(animatedValue, {
                    toValue: 1,
                    duration: 1000,
                    easing: Easing.linear,
                    useNativeDriver: true,
                }),
                Animated.timing(animatedValue, {
                    toValue: 0,
                    duration: 0,
                    useNativeDriver: true,
                }),
            ]),
        ).start();
    };

    useEffect(() => {
        const animationInterval = setInterval(() => {
            startAnimation();
        }, 1000); // אנימציה מתחילה מחדש כל שנייה (1000 מילישניות)

        return () => clearInterval(animationInterval);
    }, []);

    const interpolateValue = animatedValue.interpolate({
        inputRange: [0, 1],
        outputRange: ['0deg', '360deg'],
    });

    const animatedStyle = {
        transform: [{ rotate: interpolateValue }],
    };
    // const navigation = useNavigation();
    const onPress = () => {
        // startAnimation();
        console.log('SosButton');
        navigation.navigate('Status');
    }
    return (
        <View style={styles.container}>
            <TouchableOpacity style={styles.button} onPress={onPress}>
                <Text style={styles.buttonText}>S.O.S</Text>
                <Animated.View style={[styles.animationCircle, animatedStyle]} />
            </TouchableOpacity>
            {/* <Button
                onPress={onPress}
                title="S.O.S"
                color="#841584"
                accessibilityLabel="Learn more about this purple button"
            /> */}
        </View>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        width: 150,
        height: 150,
        borderRadius: 75,
        backgroundColor: '#3498db', // צבע רקע של הכפתור
        justifyContent: 'center',
        alignItems: 'center',
      },
    buttonText: {
        color: 'white',
        fontSize: 24,
        fontWeight: 'bold',
    },
    animationCircle: {
        position: 'absolute',
        width: 30,
        height: 30,
        borderRadius: 15,
        borderWidth: 2,
        borderColor: '#ffffff',
    },
});

export default SosButton;
