// import { useNavigation } from '@react-navigation/native';
import React, { useRef } from 'react';
import { StyleSheet, Button, View, TouchableOpacity, Animated, Easing } from 'react-native';

const SosButton = ({ navigation }) => {
    const animatedValue = useRef(new Animated.Value(0)).current;

    const startAnimation = () => {
        Animated.loop(
            Animated.sequence([
                Animated.timing(animatedValue, {
                    toValue: 1,
                    duration: 1000,
                    easing: Easing.linear,
                    useNativeDriver: true,
                }),
                Animated.timing(animatedValue, {
                    toValue: 0,
                    duration: 0,
                    useNativeDriver: true,
                }),
            ]),
        ).start();
    };

    useEffec(() => {
        const animationInterval = setInterval(() => {
            startAnimation();
        }, 1000); // אנימציה מתחילה מחדש כל שנייה (1000 מילישניות)

        return () => clearInterval(animationInterval);
    }, []);

    const interpolateValue = animatedValue.interpolate({
        inputRange: [0, 1],
        outputRange: ['0deg', '360deg'],
    });

    const animatedStyle = {
        transform: [{ rotate: interpolateValue }],
    };
    // const navigation = useNavigation();
    const onPress = () => {
        // startAnimation();
        console.log('SosButton');
        navigation.navigate('Status');
    }
    return (
        <View style={styles.container}>
            <TouchableOpacity
                style={[styles.button, animatedStyle]}
                onPress={onPress}
            />
            {/* <Button
                onPress={onPress}
                title="S.O.S"
                color="#841584"
                accessibilityLabel="Learn more about this purple button"
            /> */}
        </View>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        width: 100,
        height: 100,
        borderRadius: 50,
        backgroundColor: 'red',
    },
});

export default SosButton;
