import React, { useState, useRef } from 'react';
import { View, TextInput, StyleSheet } from 'react-native';
import CodeInput from './codeInput';

const EmailVerification = ({ onStepChange }) => {
    const [verificationCode, set] = useState(['', '', '', '', '', '']);
    const sendVerificationCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/initiate-reset`, { email })
                .then(response => {
                    console.log('Data:', response.data);
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }

    const verifyCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/email-verification`, { email , verificationCode})
                .then(response => {
                    console.log('Data:', response.data);
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error fetching data:', error);
        }

    }

    return (
        <View style={styles.container}>
            <CodeInput></CodeInput>
            <Button title='Verify' onPress={handleVerificationCode}></Button>
        </View>
    );
};

const styles = StyleSheet.create({

});

export default EmailVerification;
