import React, { useState } from 'react';
import { StyleSheet, View, TouchableOpacity, TextInput, Text } from 'react-native';
import Geolocation from '@react-native-community/geolocation';

const ProblemType = ({ route }) => {
    const level = route.params;
    console.log(level);
    const [text, setText] = useState('');
    const [location, setLocation] = useState();

    const send = () => {
        findUserLocation();
        console.log(location);
        // const location = getLocation();
        // // Do something with the location data
        // console.log(location);
    };
    const findUserLocation = () => {
        Geolocation.getCurrentPosition(
            position => {
                const { latitude, longitude } = position.coords;
                console.log('User Location:', latitude, longitude);
                setLocation({ latitude: latitude, longitude: longitude })
                console.log(location)

            },
            error => {
                console.log('Error:', error.message);
                // Handle error
            },
            { enableHighAccuracy: true, timeout: 15000, maximumAge: 10000 }
        );
    };

    return (
        <View style={styles.container}>
            <TouchableOpacity style={styles.button} onPress={send}>
                <Text style={styles.buttonText}>Problem 1</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={send}>
                <Text style={styles.buttonText}>Problem 2</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={send}>
                <Text style={styles.buttonText}>Problem 3</Text>
            </TouchableOpacity>
            <View style={styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="another"
                    onChangeText={newText => setText(newText)}
                    value={text}
                />
                <TouchableOpacity style={styles.submitButton} onPress={send}>
                    <Text style={styles.submitButtonText}>OK</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    button: {
        width: 200,
        height: 50,
        backgroundColor: 'lightblue',
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 10,
        borderRadius: 10,
    },
    buttonText: {
        color: 'white',
        fontSize: 18,
    },
    inputContainer: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    input: {
        height: 40,
        borderColor: 'gray',
        borderWidth: 1,
        width: 200,
        marginVertical: 10,
        borderRadius: 5,
        paddingHorizontal: 10,
    },
    submitButton: {
        width: 80,
        height: 40,
        backgroundColor: 'green',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginLeft: 10,
    },
    submitButtonText: {
        color: 'white',
        fontSize: 16,
    },
});

export default ProblemType;
