import React from 'react';
import { View, Text, Button, TextInput } from 'react-native';


const CompleteResetPassword = ({onStepChange}) => {
    const [pwd, onChangePwd] = React.useState('');
    const [verifyPwd, onChangeVerifyPwd] = React.useState('');

    const verifyCode = async (email) => {
        try {
            return await axios.post(`http://localhost:3000/api/reset-password/email-verification`, { email, verificationCode })
                .then(response => {
                    return response.data;
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
        catch (error) {
            console.error('Error verifing code:', error);
        }

    }

    return (
        <View>
            <Text>enter an email address:</Text>
            <TextInput
                onChangeText={onChangePwd}
                value={pwd}
            />
            <TextInput
                onChangeText={onChangeVerifyPwd}
                value={verifyPwd}
            />
            <Button title='verify password' onPress={() => console.log(pwd === verifyPwd ? "yes" : "No")}></Button>
        </View>
    )
}

export default CompleteResetPassword;
