import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Alert } from 'react-native';
import axios from 'axios';

const SignUp2 = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [country, setCountry] = useState('');
  const [city, setCity] = useState('');
  const [street, setStreet] = useState('');
  const [streetNum, setStreetNum] = useState('');
  const [entrance, setEntrance] = useState('');
  const [houseNumber, setHouseNumber] = useState('');
  const [dob, setDob] = useState('');

  const handleSignUp = async () => {
    try {
      // Validate inputs before making the API call
      if (!phoneNumber || !country || !city || !street || !streetNum || !entrance || !houseNumber || !dob) {
        Alert.alert('Error', 'All fields are required.');
        return;
      }

      // Perform API call to sign up with the entered data
      const response = await axios.post('YOUR_SIGNUP_API_ENDPOINT', {
        phoneNumber,
        address: {
          country,
          city,
          street,
          streetNum,
          entrance,
          houseNumber,
        },
        dob,
      });

      // Check the response status
      if (response.status === 200) {
        Alert.alert('Success', 'Sign-up successful!');
      } else {
        Alert.alert('Error', 'Sign-up failed. Please try again.');
      }
    } catch (error) {
      console.error('Error during sign-up:', error);
      Alert.alert('Error', 'An unexpected error occurred. Please try again later.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign Up - Step 2</Text>

      <TextInput
        style={styles.input}
        placeholder="Phone Number"
        onChangeText={(text) => setPhoneNumber(text)}
        value={phoneNumber}
        keyboardType="phone-pad"
      />

      <Text style={styles.subtitle}>Exact Residential Address:</Text>

      <TextInput
        style={styles.input}
        placeholder="Country"
        onChangeText={(text) => setCountry(text)}
        value={country}
      />

      <TextInput
        style={styles.input}
        placeholder="City"
        onChangeText={(text) => setCity(text)}
        value={city}
      />

      <TextInput
        style={styles.input}
        placeholder="Street"
        onChangeText={(text) => setStreet(text)}
        value={street}
      />

      <TextInput
        style={styles.input}
        placeholder="Street Number"
        onChangeText={(text) => setStreetNum(text)}
        value={streetNum}
      />

      <TextInput
        style={styles.input}
        placeholder="Entrance"
        onChangeText={(text) => setEntrance(text)}
        value={entrance}
      />

      <TextInput
        style={styles.input}
        placeholder="House Number"
        onChangeText={(text) => setHouseNumber(text)}
        value={houseNumber}
      />

      <TextInput
        style={styles.input}
        placeholder="Date of Birth (YYYY-MM-DD)"
        onChangeText={(text) => setDob(text)}
        value={dob}
        keyboardType="numeric"
      />

      <TouchableOpacity style={styles.button} onPress={handleSignUp}>
        <Text style={styles.buttonText}>Complete Sign Up</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
  },
  button: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default SignUp2;
